import Foundation

//MARK: API IMPLEMENTATION

enum FilmCallingError: Error {
    case problemGeneratingURL
    case problemGettingDataFromAPI
    case problemDecodingData
}

class FilmService {
    private let urlString = "https://run.mocky.io/v3/9088dfe3-d666-4eb5-9cd1-d4780f28fc78"
    // Replace with Below API link which returns an empty list to test the Implementation of Empty List Notification.
//    private let urlString = "https://run.mocky.io/v3/7dc04c8a-95fc-4ca2-afd4-0f96f76285ed"
    
    func getFilms(completion: @escaping ([Film]?, Error?) -> ()) {
            guard let url = URL(string: self.urlString) else {
                DispatchQueue.main.async { completion (nil, FilmCallingError.problemGeneratingURL) }
                return
        }
                
            let request = URLRequest(url: url)
            let task = URLSession.shared.dataTask(with: request) { data, response, error in
                guard let data = data, error == nil else {
                    DispatchQueue.main.async { completion(nil, FilmCallingError.problemGettingDataFromAPI) }
                    return
                }
                
                do {
                    let FilmResult = try JSONDecoder().decode(FilmResult.self, from: data)
                    DispatchQueue.main.async { completion(FilmResult.films, nil) }
                } catch (let error) {
                    print(error)
                    DispatchQueue.main.async { completion(nil, FilmCallingError.problemDecodingData) }
                }
                                                        
            }
            task.resume()
        }
}


